import React, { useState } from "react";

import { Button, Form, Icon, List, Message, Input } from "semantic-ui-react";

import { BASE_URL } from "./ToDo";
import Checkbox from "./Checkbox";

const ToDoEditList = props => {

  const { item, task } = props;

  const [editTask, setEditTask] = useState(null);
  const [taskError, setTaskError] = useState(false);
  const [formError, setFormError] = useState(false);

  const handleOnChange = event => {
    setEditTask(event.target.value);
  };

  const handleOnSaveClick = event => {
    event.preventDefault();

    let error = false;

    const taskLength = props.task.length;

    console.log(taskLength);

    if (taskLength < 5 || taskLength > 35) {
      setTaskError(true);
      error = true;
    } else {
      setTaskError(false);
    }

    if (error) {
      setFormError(true);
      return;
    }
    setFormError(false);

    const formattedFormData = {
      ...props.item,
      id: null,
      taskError: null,
      formError: null
    };

    fetch(`${BASE_URL}/todo/${props.item.id}.json`, {
      method: "PUT",
      body: JSON.stringify(formattedFormData)
    }).then(() => {
      props.onSave();
    });
  };

  const handleOnCloseClick = () => {
    props.onClose();
  };


  return (
    <div key={item.id}>
      <List.Item>
        <Form.Field>
          <Input
            fluid
            size="small"
            type="text"
            value={task}
            name="task"
            onChange={handleOnChange}
          />
          {taskError ? (
            <Message negative>
              <Message.Header>Incorrect input</Message.Header>
              <p>The task should be between 5 and 35 characters</p>
            </Message>
          ) : null}
        </Form.Field>
      </List.Item>
      <Button
        size="small"
        attached="left"
        positive
        icon
        onClick={handleOnSaveClick}
        disabled={!task}
      >
        <Icon name="save" />
      </Button>
      <Button size="small" attached="right" icon onClick={handleOnCloseClick}>
        <Icon name="close" />
      </Button>
      <Checkbox done={item.done} id={item.id} item={item.task} />
      <hr />
    </div>
  );
};

export default ToDoEditList;

