import React from "react";
import {
  Button,
  Form,
  Grid,
  Header,
  Segment,
  Message
} from "semantic-ui-react";
import firebase from "firebase";
import { Redirect } from "react-router";
import { Link } from "react-router-dom";

export default class Sign extends React.Component {
  state = {
    email: "",
    password: "",
    redirect: false
  };

  handleOnChange = ({ target: { name, value } }) => {
    this.setState({
      [name]: value
    });
  };

  handleOnClick = event => {
    event.preventDefault();

    this.isSignUp() ? this.signUp() : this.signIn();
  };

  isSignUp = () => {
    return this.props.match.path.includes("sign-up");
  };

  signIn = () => {
    const { email, password } = this.state;
    firebase
      .auth()
      .signInWithEmailAndPassword(email, password)
      .then(() => {
        this.setState({
          redirect: true
        });
      });
  };

  signUp = () => {
    const { email, password } = this.state;
    firebase
      .auth()
      .createUserWithEmailAndPassword(email, password)
      .then(() => {
        this.setState({
          redirect: true
        });
      });
  };

  render() {
    const { email, password, redirect } = this.state;

    if (redirect) {
      return <Redirect to={"/"} />;
    }

    return (
      <Grid
        textAlign="center"
        style={{ height: "100vh" }}
        verticalAlign="middle"
      >
        <Grid.Column style={{ maxWidth: 450 }}>
          <Header as="h3" color="yellow" textAlign="center">
            {this.isSignUp() ? "Welcome" : "Log-in to your account"}
          </Header>
          <Form size="large">
            <Segment stacked>
              <Form.Input
                fluid
                icon="user"
                iconPosition="left"
                placeholder="E-mail address"
                value={email}
                name="email"
                onChange={this.handleOnChange}
              />
              <Form.Input
                fluid
                icon="lock"
                iconPosition="left"
                placeholder="Password"
                type="password"
                value={password}
                name="password"
                onChange={this.handleOnChange}
              />

              <Button
                color="yellow"
                fluid
                size="large"
                onClick={this.handleOnClick}
              >
                {this.isSignUp() ? "Register" : "Login"}
              </Button>
            </Segment>
          </Form>
          <Message>
            {this.isSignUp() ? (
              <>
                Already register? <Link to="/sign-in">Sign In</Link>
              </>
            ) : (
              <>
                New to us? <Link to="/sign-up">Sign Up</Link>
              </>
            )}
          </Message>
        </Grid.Column>
      </Grid>
    );
  }
}
