import React from "react";
import {
  Button,
  Form,
  Grid,
  Header,
  Segment,
  Message
} from "semantic-ui-react";

import { Link } from "react-router-dom";

export default props => {
  const { email, password, onEdit, onChange, onClick } = props;

  const handleChange = () => {
    onChange();
  };

  const handleClick = () => {
    onClick();
  };

  const isSignUpCheck = () => {
    onEdit();
  };

  return (
    <Grid textAlign="center" style={{ height: "100vh" }} verticalAlign="middle">
      <Grid.Column style={{ maxWidth: 400 }}>
        <Header as="h3" color="yellow" textAlign="center">
          {isSignUpCheck() ? "Welcome" : "Log-in to your account"}
        </Header>
        <Form size="large">
          <Segment stacked>
            <Form.Input
              fluid
              icon="user"
              iconPosition="left"
              placeholder="E-mail address"
              type="text"
              autoComplete="email"
              value={email}
              name="email"
              onChange={handleChange}
            />
            <Form.Input
              fluid
              icon="lock"
              iconPosition="left"
              placeholder="Password"
              type="password"
              autoComplete="current-password"
              value={password}
              name="password"
              onChange={handleChange}
            />

            <Button color="yellow" fluid size="large" onClick={handleClick}>
              {isSignUpCheck() ? "Register" : "Login"}
            </Button>
          </Segment>
        </Form>
        <Message>
          {isSignUp ? (
            <>
              Already register? <Link to="/sign-in">Sign In</Link>
            </>
          ) : (
            <>
              New to us? <Link to="/sign-up">Sign Up</Link>
            </>
          )}
        </Message>
      </Grid.Column>
    </Grid>
  );
};
